    private void aprovalListMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_aprovalListMouseReleased
        waitingPanel.setVisible(true);
        historyPanel.setVisible(false);
        aprovalList.setForeground(new java.awt.Color(0, 0, 0));
        history.setForeground(new java.awt.Color(153, 153, 153));
        JIApprovalLeave al = new JIApprovalLeave();
        this.waitingPanel.add(al);
        al.show();
    }//GEN-LAST:event_aprovalListMouseReleased

