    private void historyMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_historyMouseClicked
        historyPanel.setVisible(true);
        waitingPanel.setVisible(false);
        announceLabel.setVisible(false);
        dashlabel.setVisible(false);
        notifPanel.setVisible(false); 
        history.setForeground(new java.awt.Color(0, 0, 0));
        aprovalList.setForeground(new java.awt.Color(153, 153, 153));
        JIApprovalHistory ah = new JIApprovalHistory();
        this.approvalHistoryPanel.add(ah);
        ah.show();
        
//        al.dispose();
    }//GEN-LAST:event_historyMouseClicked

